import React from "react";
import './write.css';

export default function write(){
    const [title, setTitle]=useState("");
    const [desc, setDesc]=useState("");
    const [file, setFile]=useState(null);
    return(
        <div className="write">
            <img src="https://images.pexels.com/photos/17005348/pexels-photo-17005348/free-photo-of-art-garden-leaf-flower.jpeg?auto=compress&cs=tinysrgb&w=600&lazy=load" 
            alt="" className="writeImg" />
            <form className="writeForm">
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                        <i className="writeIcon fa-regular fa-plus"></i>
                    </label>
                    <input type="file" id="fileInput" style={{display:"none"}}/>
                    <input type="text" placeholder="Title" className="writeInput" autoFocus={true}/>
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder="Tell your story" type="text" className="writeInput writeText"></textarea>
                </div>
                <button className="writeSubmit">Publish</button>
            </form>
        </div>
    )
}
