import React,{useContext, useEffect, useState} from "react";
import './singlePost.css';
import axios from "axios";
import { Link,useLocation } from "react-router-dom";
import { Context } from "../../context/Context";

export default function SinglePost()
{
    const location=useLocation();
    const path=(location.pathname.split("/")[2]);
    const[post,setPost]=useState({});
    const PF="http://localhost:5000/images/";
    const {user}=useContext(Context);
    const [title, setTitle]=useState("");
    const [desc, setDesc]=useState("");
    const [updateMode, setUpdateMode]=useState(false);
    useEffect(()=>{
        const getPost= async()=>{
            const res=await axios.get("/posts/"+path)
            setPost(res.data);
            setTitle(res.data.title)
            setDesc(res.data.desc)
        }
        getPost();
    }, [path]);
    
    const handleDelete=async()=>{
        try{
            await axios.delete(`/posts/${post._id}`, {
                data: {username: user.username}});
            window.location.replace("/");
        }catch(err){}
    };

    const handleUpdate=async()=>{
        try{
            await axios.post(`/posts/${post._id}`, {
                username: user.username,
                title, desc});
            window.location.reload();
        }catch(err){}
    };
    return(
        <div className="singlePost">
            <div className="singlePostWrapper">
                {post.photo &&(
                <img src={PF+post.photo}
                alt="" className="singlePostImg" />
                )}
                {
                    updateMode? <input type="text" value={title} onChange={(e)=>setTitle(e.target.value)}className="singlePostTitleInput" autoFocus/> :
                    (
                <h1 className="singlePostTitle">
                    {post.title}
                    {post.username===user?.username && (
                    <div className="singlePostEdit">
                        <i className="singlePostIcon fa-regular fa-edit" onClick={()=>setUpdateMode(true)}></i>
                        <i className="singlePostIcon fa-regular fa-trash-alt" onClick={handleDelete}></i>
                    </div>
                    )}
                </h1>
                )}
                <div className="singlePostInfo">
                    <span className="singlePostAuthor">
                        Author: 
                        <Link className="link" to={`/?user=${post.username}`}>
                        <b>{post.username}</b>
                        </Link>
                    </span>
                    <span className="singlePostDate">
                        {new Date(post.createdAt).toDateString()}
                    </span>
                </div>
                {
                    updateMode? <textarea value={desc} className="singlePostDescInput" onChange={(e)=>setDesc(e.target.value)}/> :
                    (
                <p className="singlePostDesc">
                    {post.desc}
                </p>)
                }
                { updateMode &&(
                    <button className="singlePostButton" onClick={handleUpdate}>Update</button>
                )
                }
            </div>
        </div>
    )
}
